    @BeforeEach
    void setUp() {
        guiMock = mock(GUI.class);
        roadMock = mock(Road.class);

        gameViewer = new GameViewer(roadMock);
    }

    @Test
    void roadItems() {
        gameViewer.drawRoadItems(guiMock);

        verify(guiMock).drawImage(new Position(426, 258), SIGNAL);
        verify(guiMock).drawImage(new Position(55, 258), SIGNAL);
    }

    @Test
    void HUD() {
        Joe joeMock = mock(Joe.class);
        when(joeMock.getHearts()).thenReturn(3);
        when(joeMock.getScore()).thenReturn(100);
        when(roadMock.getJoe()).thenReturn(joeMock);

        gameViewer = new GameViewer(roadMock);
        gameViewer.drawElements(guiMock);

        verify(guiMock).drawImage(new Position(0, 0), GAME_HUD);
        verify(guiMock).drawText(new Position(164, 10), 100, "#FFFFFF");

        verify(guiMock, times(3)).drawImage(any(Position.class), eq(HP));
    }

    @Test
    void roadLines() {
        int ROAD_START_Y = 38;

        // Call the method to be tested
        gameViewer.drawRoadLines(guiMock);

        // Verify that the necessary methods from GUI were called with the expected parameters
        verify(guiMock).setColorHexaCode("#C0BBB1");
        verify(guiMock).fillRectangle(new Position(0, ROAD_START_Y), 150, 500 - ROAD_START_Y);
        verify(guiMock).fillRectangle(new Position(850, ROAD_START_Y), 150, 500 - ROAD_START_Y);

        int iniX = 167;
        for (int i = 0; i < 834 - iniX - 15; i += 24) {
            verify(guiMock).fillRectangle(new Position(iniX + i, 368), 16, 50);
        }

        verify(guiMock).fillRectangle(new Position(326, ROAD_START_Y), 4, 302);
        verify(guiMock).fillRectangle(new Position(500, ROAD_START_Y), 4, 302);
        verify(guiMock).fillRectangle(new Position(674, ROAD_START_Y), 4, 302);

        verify(guiMock, times(1)).setColorHexaCode("#3D3638");
        verify(guiMock, times(37)).fillRectangle(any(Position.class), anyInt(), anyInt());
    }

    @Test
    void drawElements() {
        Car carMock = mock(Car.class);
        Joe joeMock = mock(Joe.class);
        Kid kidMock = mock(Kid.class);

        when(roadMock.getCars()).thenReturn(Arrays.asList(carMock));
        when(roadMock.getJoe()).thenReturn(joeMock);
        when(roadMock.getKids()).thenReturn(Arrays.asList(kidMock));

        GameViewer gameViewer = new GameViewer(roadMock);
        gameViewer.drawElements(guiMock);
    }



